<template>    <ul class="nav navbar-nav">        <li v-for="link in links" v-on:click="close()">            <router-link :to="{name : link.name}">                <i :class="[link.icon]"></i> {{ link.title }}            </router-link>        </li>    </ul></template><script>  export default {    name: "Menu",    data () {      return {         links: [           {             title: 'Dashboard',             name: 'dashboard',             icon: 'fa fa-fw fa-dashboard'           },           {             title: 'Profile',             name: 'profile',             icon: 'fa fa-fw fa-user'           },           {             title: 'Wallets',             name: 'wallets',             icon: 'fa fa-fw fa-money'           },           {             title: 'Categories',             name: 'categories',             icon: 'fa fa-fw fa-list'           },           {             title: 'Orders',             name: 'orders',             icon: 'fa fa-fw fa-inbox'           }         ]      }    },    methods: {      close() {        $('#navbar-collapse').removeClass('navbar-collapse pull-left collapse show in').addClass('navbar-collapse pull-left collapse');      }    }  }</script><style scoped>    .active {        background: rgba(0,0,0,0.1);        color: #f6f6f6;    }</style>