<template>    <div class="row">    <div class="col-sm-12">        <div class="box box-primary">            <!--<div class="box-header with-border">-->            <!--<h3 class="box-title">Bordered Table</h3>-->            <!--</div>-->            <!--<div class="box-body">-->                <!--<button type="button" class="btn btn-sm btn-primary" v-on:click="openModal('Add new Order')">-->                    <!--<i class="fa fa-fw fa-plus"></i> Add new order-->                <!--</button>-->            <!--</div>-->            <!-- /.box-header -->            <div class="box-body table-responsive">                <table class="table table-bordered">                    <tbody>                    <tr>                        <th class="text-center" title="Category">                            <i class="fa fa-fw fa-list"></i>                        </th>                    </tr>                    <tr v-for="category in categories">                        <td class="text-center">                            <span>{{ category.name }}</span>                        </td>                    </tr>                    </tbody>                </table>            </div>            <!-- /.box-body -->            <loading :loading="loading"></loading>            <!-- /.box-body -->            <div class="box-footer clearfix text-center">                <pagination :data="paginationData" @pagination-change-page="getCategories"></pagination>            </div>        </div>    </div>    </div></template><script>  import { getCall } from '../Api/utils/Endpoint';  import CategoryApi from '../Api/CategoryApi';  import pagination from "../Helpers/Pagintaion";  import loading from "../Helpers/LoadingComponent";  export default {    name: "CategoryComponent",    components: {      pagination,      loading    },    data() {      return {        categories: {},        paginationData: {},        loading: false      }    },    mounted() {      this.getCategories();    },    methods: {        getCategories(page = 1) {          this.loading = true;          getCall(this.$root.$data.apiUrl + CategoryApi.index + '?page=' + page,  this.$root.$data.userId)            .then(response => {              this.loading = false;              this.categories = response.data.categories.data;              this.paginationData = response.data.categories;            })            .catch(error => {              this.loading = false;              swal(error.response.data.message, "", "error");            });        }    }  }</script><style scoped></style>