<template>    <div class="row">    <div class="col-sm-12">        <div class="box box-primary">            <div class="box-body table-responsive">                <table class="table table-bordered">                    <tbody>                    <tr>                        <th class="text-center" title="Category">                            <div class="hidden-xs">                                Category                            </div>                            <div class="visible-xs">                                <i class="fa fa-fw fa-list"></i>                            </div>                        </th>                        <th class="text-center" title="Category">                            <div class="hidden-xs">                                Actions                            </div>                            <div class="visible-xs">                                <i class="fa fa-fw fa-cog"></i>                            </div>                        </th>                    </tr>                    <tr v-for="(category, index) in categories">                        <td class="text-center">                            <span>{{ category.name }}</span>                        </td>                        <td>                            <button type="button" title="Edit" class="btn btn-primary btn-xs"><i class="fa fa-fw fa-pencil-square-o"></i></button>                            <DeleteButtonComponent :recordId="category.id" :endpoint="apiUrl" entity="categories" :index="index" :loading="true"></DeleteButtonComponent>                        </td>                    </tr>                    </tbody>                </table>            </div>            <!-- /.box-body -->            <loading :loading="loading"></loading>            <!-- /.box-body -->            <div class="clearfix text-center">                <pagination :data="paginationData" @pagination-change-page="getCategories"></pagination>            </div>        </div>    </div>    </div></template><script>  import { getCall } from '../Api/utils/Endpoint';  import CategoryApi from '../Api/CategoryApi';  import pagination from "../Helpers/Pagintaion";  import loading from "../Helpers/LoadingComponent";  import DeleteButtonComponent from "../Helpers/Buttons/DeleteButtonComponent";  export default {    name: "CategoryComponent",    components: {      DeleteButtonComponent,      pagination,      loading    },    data() {      return {        categories: {},        paginationData: {},        loading: false,      }    },    mounted() {      this.getCategories();      this.apiUrl = this.$root.$data.apiUrl;    },    methods: {        getCategories(page = 1) {          this.loading = true;          getCall(this.$root.$data.apiUrl + CategoryApi.index + '?page=' + page,  {user: this.$root.$data.userId})            .then(response => {              this.loading = false;              this.categories = response.data.categories.data;              this.paginationData = response.data.categories;            })            .catch(error => {              this.loading = false;              swal(error.response.data.message, "", "error");            });        }    }  }</script><style scoped></style>