<template>    <div class="col-md-6">        <div class="box box-primary">            <div class="box-header with-border">                <div class="box-body">                    <line-chart                        v-if="loaded"                        :chartdata="chartdata"                        height="343"                    />                </div>                <loading :loading="loading"></loading>            </div>        </div>    </div></template><script>  import LineChart from '../Helpers/Charts/LineChart';  import loading from "../Helpers/LoadingComponent";  export default {    name: "SpentMoneyByMonth",    components: {      'line-chart': LineChart,      loading    },    data () {      return {        loading: true,        loaded: false,        chartdata: null      }    },    mounted () {        this.loaded = false;        (async () => {          try {            this.chartdata = await this.getData();            this.loaded = true;            this.loading = false;          } catch (e) {            console.log(e)          }        })();    },    methods: {        async getData () {          const res = await axios.get(this.$root.$data.apiUrl + "/dashboard/getSumOrdersByMonth?limit=4", {            headers: {              user: this.$root.$data.userId            }          });          return await res.data;        }    }  }</script><style scoped></style>