<template>    <button type="button" class="btn btn-danger btn-xs" title="Delete" v-on:click="destroy">        <i class="fa fa-fw fa-trash-o"></i>    </button></template><script>  export default {    name: "DeleteButtonComponent",    props: {      recordId: {        type: Number      },      index: {        type: Number,        required: true      },      title: {        type: String,        default: 'Are you sure?'      },      text: {        type: String,        default: 'Once deleted, you will not be able to recover this record!'      },      endpoint: {        type: String,        required: true      },      entity: {        type: String,        required: true      },      loading: {        type: Boolean,        default: false      }    },    methods: {      destroy() {        // console.log(this.$parent.$data[this.entity].splice(this.index, 1));        swal({          title: this.title,          text: this.text,          icon: "warning",          buttons: true,          dangerMode: true        }).then(willDelete => {          if (willDelete) {            if (this.loading) {              this.$parent.$data.loading = true;            }            axios              .post(                this.endpoint + '/' +                this.entity + '/' +                this.recordId +                "?_method=DELETE"              )              .then(response => {                if (this.loading) {                  this.$parent.$data.loading = false;                }                this.$parent.$data[this.entity].splice(this.index, 1);                swal(response.data.message, "", "success");              })              .catch(error => {                if (this.loading) {                  this.$parent.$data.loading = false;                }                if (error.response.status === 400) {                  swal(error.response.data.message, "", "error");                }              });          }        });      }    }  }</script><style scoped></style>