<script>  import { Bar } from 'vue-chartjs'  export default {    extends: Bar,    props: {      labels: {        type: Array,        required: false      },      datasets: {        type: Array      }    },    mounted () {      this.renderChart({        labels: ['November 2018', 'December 2018', 'January 2019'],        datasets: [          {            label: 'Spent money by month',            backgroundColor: '#3B8DBB',            data: [49810, 62443, 1675]          }        ]      })    }  }</script>