<template>    <div>        <input type="file" @change="onFileSelected" :v-bind="selectedFile">    </div></template><script>  import { postCall } from '../Api/utils/Endpoint';  export default {    name: "UploadImage",    props: ['endpoint'],    data () {      return {        selectedFile: {}      }    },    methods: {      onFileSelected(event) {        this.selectedFile = event.target.files[0];        const formData = new FormData();        formData.append('image', this.selectedFile, this.selectedFile.name);        postCall(this.$root.$data.apiUrl + this.endpoint, formData)          .then(response => {            this.selectedFile = null;            this.$parent.$data.uploadImages.uploadedImages.push(response.data);          })          .catch(error => {            console.log(error.response.data);            // swal(error.response.data.message, "", "error");          });      }    }  }</script><style scoped></style>